import React from 'react'
import List from './ListCategory'

class SideBar extends React.Component{

    render() {
        let list = List;
        return <div className="pad">{
            list.map((value => {
                return <div key={value.id}>
                <LabelTree value={value}/>
                </div>

            }))
        }
        </div>
    }


}

function LabelTree(props) {

    if(props.value.children) {
        let child = props.value.children;
        console.log(child);
        child.map((item =>{
            return <div id={item.id}  >
                    <label className="containerForTree">{item.name}
                        <input type="checkbox" />
                        <span className="checkmark"></span>
                    </label>
                </div>
        }))
    }
    return(
        <div id={props.value.id}  >
        <label className="containerForTree">{props.value.name}
            <input type="checkbox" />
            <span className="checkmark"></span>
        </label>
        </div>
    )
}

export default SideBar
