import React from 'react'
import List from './ListCategory'

class SideBar extends React.Component{
    constructor(){
        super()
        this.state ={
            data: [],
        }
    }

    componentDidMount() {
        fetch("http://localhost:8080/api")
            .then(response => {
                return  response.json();
            })
            .then(result => {
                this.setState({data:result[0].listSubCategory});
                console.log(result);

            })
            .catch(error => {
                console.log("MyErrorInApp ", error)
            })
    }

    render() {
        let list = data;
        return <div className="pad">{
            list.map((value => {
                if (value.children) {
                    let list2 =  value.children;
                    list2.map((item => {
                           // console.log(item);
                            return <div >
                                {item.name}
                            </div>
                        }
                    ))
                }
                return <div key={value.id}>
                <LabelTree value={value}/>
                </div>

            }))
        }
        </div>
    }


}

function LabelTree(props) {


    return(
        <div id={props.value.id}   >
        <label className="containerForTree">{props.value.name}
            <input type="checkbox" />
            <span className="checkmark"></span>
        </label>
        </div>
    )


}

export default SideBar
