import Header from './Header'
import React from 'react';
import logo from './logo.svg';
import './App.css';
import SideBar from "./SideBar";
import MaineContent from "./MaineContent";


class App extends React.Component{
    constructor(props){
        super(props)
        this.state ={
            data: [],
            isLoading : true
        }
    }
//-----------get request from api Content House-----------------------
    componentDidMount() {
        fetch("http://localhost:8080/api/tree")
            .then(response => {
                return response.json();
            })
            .then(result => {
                this.setState({
                    data : result,
                    isLoading : false,
                })
            })
            .catch(error => {
                console.log("MyErrorInFetch : "+error)
            })
    }

//------------------------------------------------------------------------
    render() {
        const {data} = this.state;

    return (

        <div id="wrapper" className="container">

            <Header/>
            <div id="category" className="row">
                {
                    this.state.isLoading &&
                    <i className="fa fa-spinner fa-spin">Придумать заглушку загрузки</i>
                }
                {
                    !this.state.isLoading &&
                    <SideBar data={data}/>
                }
                <div id="featured" className="col-md-9">
                    <MaineContent/>
                </div>
            </div>
            <div id="footer">
                <div className="footer-top row">
                    <div className="menu-footer col-sm-6 col-md-3">
                        <div className="well">3<br/>.menu-footer</div>
                    </div>
                    <div className="menu-footer col-sm-6 col-md-3">
                        <div className="well">3<br/>.menu-footer</div>
                    </div>
                    <div className="menu-footer col-sm-6 col-md-3">
                        <div className="well">3<br/>.menu-footer</div>
                    </div>
                    <div className="menu-footer col-sm-6 col-md-3">
                        <div className="well">3<br/>.menu-footer</div>
                    </div>
                </div>
                <div className="footer-bottom row">
                    <div className="copyrights col-sm-6 col-md-6">
                        <div className="well">6<br/>.copyrights</div>
                    </div>
                    <div className="copyrights col-sm-6 col-md-6">
                        <div className="well">6<br/>.copyrights</div>
                    </div>
                </div>
            </div>
        </div>);
    }
}

export default App;
