import Header from './Header'
import React from 'react';
import logo from './logo.svg';
import './App.css';
import SideBar from "./SideBar";
import MaineContent from "./MaineContent";


class App extends React.Component{
    constructor(props){
        super(props)
        this.state ={
            tree_data: [],
            isLoadingTree : true,
            prod_data : [],
            isLoadingProd : true,
        }
    }
//-----------get request from api Content House-----------------------
    componentDidMount() {
        fetch("http://localhost:8080/api/tree")
            .then(response => {
                return response.json();
            })
            .then(result => {
                this.setState({
                    tree_data : result,
                    isLoadingTree : false,
                })
            })
            .catch(error => {
                console.log("MyErrorInFetch tree : "+error)
            })
        //------End load tree data---------------------------------------
        //------Start load prod loading----------------------------------
        fetch("http://localhost:8080/api/prod")
            .then(response => {
                return response.json();
            })
            .then(result => {
                this.setState({
                    prod_data : result,
                    isLoadingProd : false,
                })
                console.log(JSON.stringify(result))
            })
            .catch(error => {
                console.log("MyErrorInFetch prod : "+error)
            })
    }

//------------------------------------------------------------------------
    render() {
        const {tree_data, prod_data} = this.state;

    return (

        <div id="wrapper" className="container">

            <Header/>
            <div id="category" className="row">
                {
                    this.state.isLoadingTree &&
                    <i className="fa fa-spinner fa-spin">Придумать заглушку загрузки</i>
                }
                {
                    !this.state.isLoadingTree &&
                    <SideBar data={tree_data}/>
                }
                <div id="featured" className="col-md-9">
                    {
                        this.state.isLoadingProd &&
                        <i className="fa fa-spinner fa-spin">Придумать заглушку загрузки</i>
                    }
                    {
                        !this.state.isLoadingProd &&
                            <MaineContent data={prod_data}/>
                    }
                </div>
            </div>
            <div id="footer">
                <div className="footer-top row">
                    <div className="menu-footer col-sm-6 col-md-3">
                        <div className="well">3<br/>.menu-footer</div>
                    </div>
                    <div className="menu-footer col-sm-6 col-md-3">
                        <div className="well">3<br/>.menu-footer</div>
                    </div>
                    <div className="menu-footer col-sm-6 col-md-3">
                        <div className="well">3<br/>.menu-footer</div>
                    </div>
                    <div className="menu-footer col-sm-6 col-md-3">
                        <div className="well">3<br/>.menu-footer</div>
                    </div>
                </div>
                <div className="footer-bottom row">
                    <div className="copyrights col-sm-6 col-md-6">
                        <div className="well">6<br/>.copyrights</div>
                    </div>
                    <div className="copyrights col-sm-6 col-md-6">
                        <div className="well">6<br/>.copyrights</div>
                    </div>
                </div>
            </div>
        </div>);
    }
}

export default App;
